import kotlin.system.exitProcess

var intentos = 10

fun limpiarConsola() {
    repeat(20) { println() }  // Imprime 50 saltos de línea
}

fun pokemon(lista: List<String>): List<String> {
    val pokemonAleatorio = lista.random()
    val oculto = pokemonAleatorio.replace(Regex("[A-Za-z]"), "*")
    val resultado = listOf(pokemonAleatorio, oculto)
    return resultado
}

fun juego(lista: List<String>) {
    // Seleccionar el Pokémon oculto
    val pokemonOculto = pokemon(lista)
    val pokemon = pokemonOculto[0]

    // Inicializar variables necesarias
    val stringBuilder = StringBuilder(pokemonOculto[1])
    if (pokemon.length > intentos) intentos = pokemon.length

    val letrasRestantes = (('A'..'N').map { it.toString() } + "Ñ" + ('O'..'Z').map { it.toString() }).toMutableList()

    // Juego principal
    for (intentosRestantes in intentos downTo 0) {
        limpiarConsola()

        //Pintar dibujo
        when(intentosRestantes){
            0 -> {
                DibujoAhorcado.dibujar(7)
                break
            }
            1 -> DibujoAhorcado.dibujar(6)
            2 -> DibujoAhorcado.dibujar(5)
            3 -> DibujoAhorcado.dibujar(4)
            4 -> DibujoAhorcado.dibujar(3)
            5 -> DibujoAhorcado.dibujar(2)
            6 -> DibujoAhorcado.dibujar(1)
            else -> DibujoAhorcado.dibujar(1)
        }

        // Mostrar el estado actual del juego
        println("El nombre del Pokémon es: ${stringBuilder},")
        println("Te quedan $intentosRestantes intentos")
        println("Letras restantes: $letrasRestantes\n")

        print("Ingrese una letra:")


        // Leer y procesar la letra ingresada
        val letra = comprobarLetra(letrasRestantes)
        letrasRestantes.remove(letra.uppercase())

        if (letra in pokemon || letra.uppercase() in pokemon) {
            revelar(pokemon, stringBuilder, letra)
        }

        // Verificar si el jugador ha ganado
        if (pokemon == stringBuilder.toString()){
            println("Victoria!")
            println("Felicidades adivinaste el nombre del pokemon con $intentosRestantes intentos restantes!")
            break
        }
        limpiarConsola()
    }

    // Verificar si el jugador ha perdido
    if (pokemon != stringBuilder.toString()){
        println("Derrota")
        println("El nombre del pokemon era $pokemon")
    }

    // Preguntar si desea volver a jugar
    println("Desea volver a jugar? Ingrese 1 para si 0 para No")
    val selector = readln().toInt()
    if (selector != 1) exitProcess(0)
}

fun comprobarLetra(lista: MutableList<String>): String {
    while (true) {
        println("Ingrese una letra:")
        val letra = readln().uppercase()

        // Validar que sea una sola letra
        if (letra.length != 1) {
            println("Por favor, ingrese solo una letra.")
            continue
        }

        // Validar que la letra esté en la lista
        if (!lista.contains(letra)) {
            println("La letra '$letra' no está disponible o ya ha sido usada. Intente nuevamente.")
            continue
        }

        // Letra válida
        return letra.lowercase()
    }
}

fun revelar(pokemon:String, stringBuilder:StringBuilder, letra:String ){
    for (i in pokemon.indices){
        if (pokemon[i].toString() == letra){
            stringBuilder.setCharAt(i, letra[0])
        }
        if (pokemon[i].toString() == letra.uppercase()){
            stringBuilder.setCharAt(i, letra.uppercase()[0])
        }
    }
}

fun dificultad() {
    println("La dificultad por defecto es normal")
    println("Selecciona el nivel de dificultad:")
    println("1 - Fácil (18 intentos)")
    println("2 - Normal (10 intentos)")
    println("3 - Difícil (5 intentos)")
    println("4 - Personalizado (Elige cuantos intentos deseas)\n")
    println("Se asegura que tendrás al menos la misma cantidad de intentos que letras tiene el nombre del Pokémon.\n")

    print("Elige una opción (1-4):")

    val selector = readln().toInt()
    when (selector) {
        1 -> {
            println("Facil - 18")
            intentos = 18
        }

        2 -> {
            println("Normal - 10")
            intentos = 10
        }

        3 -> {
            println("Dificil - 5")
            intentos = 5
        }

        4 -> {
            println("Personalizado - Elige cuantos intentos deseas")
            val selector2 = readln().toInt()
            intentos =  selector2
        }
    }
    return
}

